<?php require('../Aspecto/libreria.inc');cabecera_alta();function LimpiaResultados($objeto){	foreach ($objeto as $atributo => $valor)		if(is_string($objeto->$atributo) === true)			$objeto->$atributo = stripslashes($objeto->$atributo);}class ExcepcionEnTransaccion extends Exception{	public function __construct(){}}?><?phpif (isset($_GET['alta'])===true) { $cedula=$_GET['cedula'];$nombre=$_GET['nombre'];try{	@ $db = new mysqli('localhost', 'root', 'root');	if (mysqli_connect_errno() != 0)		throw new Exception('Error conectando:'.mysqli_connect_error(), mysqli_connect_errno());		$db->select_db('clinica');	if ($db->errno != 0)		throw new Exception('Error seleccionando bd:'.$db->error, $db->errno);		$consulta = "insert into medicos(cedula,nombre_medico) values ('$cedula','$nombre')";	if ($db->query($consulta) === false)		throw new ExcepcionEnTransaccion();	$db->commit();		$consulta = "select cedula,nombre_medico from medicos";	$resultado = $db->query($consulta);	if ($db->errno != 0)		throw new Exception('Error realizando consulta:'.$db->error, $db->errno);	assert($resultado !== false);	if ($resultado->num_rows > 0){			echo '<table border="1">';			echo'<tr>';				echo'<th>CEDULA</th>';				echo'<th>NOMBREMEDICO</th>';			echo'</tr>';			while ($obj = $resultado->fetch_object()){				LimpiaResultados($obj);				echo'<tr>';					echo'<td align="center">'.$obj->cedula.'</td>';					echo'<td align="center">'.$obj->nombre_medico.'</td>';				echo'</tr>';			}			echo'</table>';		}	else echo '<p>No hay datos que mostrar</p>';	?>	<p id="n_alta">[<a href="<?php echo $_SERVER['alta_medico.html'] ?>">Nueva alta</a>]</p>	<?php	$resultado->free(); 	$db->close(); }catch (ExcepcionEnTransaccion $e){	echo 'No se ha podido realizar al alta';	$db->rollback();	$db->close();}catch (Exception $e){	echo $e->getMessage();	if (mysqli_connect_errno() == 0)		$db->close();	exit();}}?>