<?php require('../aspecto/libreria.inc');cabecera_baja();function LimpiaResultados($objeto){	foreach ($objeto as $atributo => $valor)		if(is_string($objeto->$atributo) === true)			$objeto->$atributo = stripslashes($objeto->$atributo);}class ExcepcionEnTransaccion extends Exception{	public function __construct(){}}?><!DOCTYPE HTML PUBLIC "-//W3C//DTD HTML 4.01 Frameset//EN"><html><head>	<meta http-equiv="Content-Type" content="text/html; charset=utf-8">	<title>Bajas de Médicos</title></head><body><?phpif (isset($_GET['elimina'])===false) {try{	@ $db = new mysqli('localhost', 'root', 'root');	if (mysqli_connect_errno() != 0)		throw new Exception('Error conectando:'.mysqli_connect_error(), mysqli_connect_errno());		$db->select_db('clinica');	if ($db->errno != 0)		throw new Exception('Error seleccionando bd:'.$db->error, $db->errno);			$consulta = "select id_medico from medicos";	$resultado = $db->query($consulta);	if ($db->errno != 0)		throw new Exception('Error realizando consulta:'.$db->error, $db->errno);	assert($resultado !== false);	if ($resultado->num_rows > 0){	?>		<form id="bajas" action="<?php echo $_SERVER['PHP_SELF'] ?>" method="get">				<p>IDMedico: 				<select name="id">	<?php		while ($obj = $resultado->fetch_object()){			LimpiaResultados($obj);	?>			<option value="<?php echo $obj->id_medico ?>" ><?php echo $obj->id_medico ?></option>			<?php		}	?>					</select>				</p>				<INPUT TYPE="submit" NAME="elimina" VALUE="Elimina encuesta">		</FORM>	<?php		}	else		echo '<p>No hay datos que mostrar</p>';$resultado->free(); $db->close();}catch (Exception $e){	echo $e->getMessage();	if (mysqli_connect_errno() == 0)		$db->close();	exit();}} else {$idmedico=$_GET['id']; try{	@ $db = new mysqli('localhost', 'root', 'root');	if (mysqli_connect_errno() != 0)		throw new Exception('Error conectando:'.mysqli_connect_error(), mysqli_connect_errno());		$db->select_db('clinica');	if ($db->errno != 0)		throw new Exception('Error seleccionando bd:'.$db->error, $db->errno);		$consulta = "delete from medicos where id_medico='".$idmedico."'";	if ($db->query($consulta) === false)		throw new ExcepcionEnTransaccion();	$db->commit();		$consulta = "select id_medico,cedula,nombre_medico,usu_medico,pass_medico from medicos";	$resultado = $db->query($consulta);	if ($db->errno != 0)		throw new Exception('Error realizando consulta:'.$db->error, $db->errno);	assert($resultado !== false);	if ($resultado->num_rows > 0){			echo '<table border="1">';			echo'<tr>';				echo'<th>IDMEDICO</th>';				echo'<th>CEDULA</th>';				echo'<th>NOMBRE</th>';				echo'<th>USUARIO</th>';				echo'<th>CONTRASEÑA</th>';			echo'</tr>';			while ($obj = $resultado->fetch_object()){				LimpiaResultados($obj);				echo'<tr>';					echo'<td align="center">'.$obj->id_medico.'</td>';					echo'<td align="center">'.$obj->cedula.'</td>';					echo'<td align="center">'.$obj->nombre_medico.'</td>';					echo'<td align="center">'.$obj->usu_medico.'</td>';					echo'<td align="center">'.$obj->pass_medico.'</td>';				echo'</tr>';			}			echo'</table>';		}	else		echo '<p>No hay datos que mostrar</p>';	?>		<p>[<a href="<?php echo $_SERVER['PHP_SELF'] ?>">Nueva baja</a>]</p>	<?php	$resultado->free(); 	$db->close();}catch (ExcepcionEnTransaccion $e){	echo 'No se ha podido realizar la baja';	$db->rollback();	$db->close();}catch (Exception $e){	echo $e->getMessage();	if (mysqli_connect_errno() == 0)		$db->close();	exit();}}?></body></html>